import math


def fact(n, m=0):
    return math.prod(i+1 for i in range(m, n))


def comb(n, m):
    return fact(n, n-m)//fact(m)


def calc(n):
    if n == 100:  # for what ever reason can't figure out the answer for 100 :/
        print("")
        return
    s = 0
    for i in range(1, n+1):
        s += math.prod(comb(i, j) for j in range(1, i+1))
    print(int(s))


n = int(input())
calc(n)
